    'webApps.useCases.crm.title': 'CRM und Kundensupport',
    'webApps.useCases.crm.description': 'Verfolgen Sie Kundeninteraktionen, verwalten Sie Beziehungen.',
    'webApps.useCases.automation.title': 'Automatisierung',
