    'homepage.features.personalization.title': 'Personalizace',
    'homepage.features.personalization.description': 'Vaše webové stránky budou tak jedinečné, jako vaše značka – žádné šablóny, žádná klišé.',
    'homepage.features.communication.title': 'Komunikace',
