// Provider component
export function TranslationProvider({ children }: { children: React.ReactNode }) {
  // Always start with default language to avoid hydration mismatch
  const [currentLanguage, setCurrentLanguage] = useState<Language>('sk');

  // Load language from localStorage after hydration
  useEffect(() => {
    if (typeof window !== 'undefined') {
      const savedLanguage = localStorage.getItem('language') as Language;
      if (savedLanguage && ['sk', 'en', 'de', 'cs'].includes(savedLanguage)) {
        setCurrentLanguage(savedLanguage);
      }
    }
  }, []);

  const setLanguage = (lang: Language) => {
    setCurrentLanguage(lang);
    if (typeof window !== 'undefined') {
      localStorage.setItem('language', lang);
    }
  };

  const t = (key: string): string => {
    const languageTranslations = translations[currentLanguage];
    return (languageTranslations as Record<string, string>)[key] || key;
  };

  return (
    <TranslationContext.Provider value={{ currentLanguage, setLanguage, t }}>
      {children}
    </TranslationContext.Provider>
  );
}

// Hook to use translation
export function useTranslation() {
  const context = useContext(TranslationContext);
  if (context === undefined) {
    throw new Error('useTranslation must be used within a TranslationProvider');
  }
  return context;
} 